import './App.css'
import {useState} from 'react';

function App() {
  // todolist 화면에 출력되는 (추가, 삭제, 수정)
  const [todos, setTodos] = useState([
    {id: 1, task: '투두 만들어보기'}, 
    {id: 2, task: 'abcde'}
  ]);

  const [text,setText] = useState('');
  const [editingId, seteditingId] = useState('');
  const [editText, seteditText] = useState('');

  //추가하기

  const addTodo = () => {
    setTodos( (prev) => [
      ...prev, 
      {id: Math.floor(Math.random()*100)+2, task : text}
    ]) 
    setText('');
  };

  //삭제하기

  const deleteTodo = (id) => {
    setTodos((prev) => prev.filter((item) => item.id !== id));
  };

  //수정하기

  const updataTodo = (id, text) => {
    setTodos((prev) => 
    prev.map((item) => item.id === id ? {...item, task:text}: item)
    );
  }

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input 
          type='text' 
          value={text} 
          onChange={(e) => setText(e.target.value)}
        />
        <button onClick={() => addTodo()} type='submit'>할 일 등록</button>
      </form>
      <div>
        {todos.map((todo, _) => (
        <div style={{display: 'flex', gap: '20px'}}>
        {/* 수정중 아닐때 */}
        {editingId !== todo.id && (<div key={todo.id} style={{display: 'flex', gap: '5px'}}> 
          <p>{todo.id}. </p>
          <p>{todo.task} </p>
        </div>)}
        {/* 수정중일때 */}
        {editingId === todo.id && (<div key={todo.id} style={{display: 'flex', gap: '5px'}}> 
          <p>{todo.id}. </p>
          <input defaultValue={todo.task} onChange={(e) => seteditText(e.target.value)}/>
        </div>)}
        <button onClick={() => deleteTodo(todo.id) }>삭제하기</button>
        {/* editingId !== todo.id 수정이 아닌상태*/ }
        {/* editingId === todo.id 수정중인 상태*/ }
        {editingId === todo.id ? (
          <button onClick={() => updataTodo(editingId,editText)} >수정 완료</button>
        ) : (<button onClick={() => seteditingId(todo.id)}>수정 진행</button>
        )}
        </div>
        ))}
      </div>
    </>
  )
}

export default App;
