import './App.css'
import {useState} from 'react';

function App() {
  const [todos, setTodos] = useState([
    {id: 1, task: '투두 만들어보기'}, 
    {id: 2, task: 'abcde'}
  ]);

  console.log(todos);

  const handleSubmit = (e) => {
    e.preventDefault();
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input type='text' />
        <button type='submit'>할 일 등록</button>
      </form>
      <div>
        {todos.map((todo, _) => (
        <h4> 
          {todo.id}.{todo.task} 
        </h4>
        ))}
      </div>
    </>
  )
}

export default App;
