import './App.css';
import { useState } from 'react';
import Button from './Button';
import Input from './Input';

function App() {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState('');
  const [editingId, seteditingId] = useState('');
  const [editText, seteditText] = useState('');

  // 추가하기
  const addTodo = () => {
    if (text.trim().length === 0) {
      alert('빈 문자는 입력할 수 없습니다');
      return 0;
    }
    setTodos((prev) => [
      ...prev, 
      { id: Math.floor(Math.random() * 100) + 2, task: text }
    ]);
    setText('');
  };

  // 삭제하기
  const deleteTodo = (id) => {
    setTodos((prev) => prev.filter((item) => item.id !== id));
  };

  // 수정하기
  const updateTodo = (id, text) => {
    if (text.trim().length === 0) {
      alert('빈 문자는 입력할 수 없습니다');
      return 0;
    }
    setTodos((prev) => 
      prev.map((item) => (item.id === id ? { ...item, task: text } : item))
    );
    seteditingId('');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <Input 
          value={text} 
          onChange={(e) => setText(e.target.value)} 
          placeholder="할 일을 입력하세요"
        />
        <Button onClick={addTodo} type="submit">할 일 등록</Button>
      </form>
      <div>
        {todos.map((todo) => (
          <div key={todo.id} style={{ display: 'flex', gap: '20px' }}>
            {/* 수정 중 아닐 때 */}
            {editingId !== todo.id && (
              <div style={{ display: 'flex', gap: '5px' }}>
                <p>{todo.id}.</p>
                <p>{todo.task}</p>
              </div>
            )}
            {/* 수정 중일 때 */}
            {editingId === todo.id && (
              <div style={{ display: 'flex', gap: '5px' }}>
                <p>{todo.id}.</p>
                <Input 
                  defaultValue={todo.task} 
                  onChange={(e) => seteditText(e.target.value)} 
                />
              </div>
            )}
            <Button onClick={() => deleteTodo(todo.id)}>삭제하기</Button>
            {editingId === todo.id ? (
              <Button onClick={() => updateTodo(editingId, editText)}>수정 완료</Button>
            ) : (
              <Button onClick={() => seteditingId(todo.id)}>수정 진행</Button>
            )}
          </div>
        ))}
      </div>
    </>
  );
}

export default App;
